        private void FormHoaDonChiTiet_Load(object sender, EventArgs e)
        {
            DataTable dt = new DataTable();
            con = DAO_DATABASE.OpenConnect();
            dt = SqlHelper.ExecuteDataset(con, "PR_HOADONCHITIET_XEMCHITIET", txtMaphieu.Text).Tables[0];
            grcHDCT.DataSource = dt;

        }

