        private void btnThem_Click(object sender, EventArgs e)
        {
            try
            {
                con = DAO_DATABASE.OpenConnect();
                SqlHelper.ExecuteNonQuery(con, "PR_HOADONCHITIET_THEM", txtMaphieu.Text, txtMaMH.Text, txtNhanVien.Text, txtSoLuong.Text, txtMaLoaiTien.Text);
                DAO_DATABASE.CloseConnect(con);
                FormHoaDonChiTiet_Load(sender,e);

            }
            catch(Exception ex) {
                MessageBox.Show("Loi");
            }
        }

        private void btnXoa_Click(object sender, EventArgs e)
